import React, { Component } from 'react';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import axios from 'axios';
import alertify from 'alertifyjs';
import '../../../css/alertify.css';

class FormPurchase extends Component {
  	
  	constructor(props) {
        super(props);
        this.state = {nombre: ''};
        this.state = {precio: ''}; 
        this.handleSaveButton = this.handleSaveButton.bind(this);
    }
    handleCancelButton(){
        this.props.funcionClick('PurchaseTypes');        
    }
    handleSaveButton(id){
        //GUARDAR/EDITAR LA INFORMACION
        
        if(this.state.nombre === undefined || this.state.nombre === ''){
            alertify.error('Digite el nombre!'); 
            return;
        }   
        else if(this.state.precio === undefined || this.state.precio === ''){
            alertify.error('Digite el precio!'); 
            return;
        }

        var userData = {
            id : id,
            nombre : this.state.nombre,
            precio : this.state.precio
        };

        var url = '';

        if(id>0){
            url = '';            
        }
        else{
            url = 'purchase_types';
        } 

        axios({
            method: 'post',
            url: 'http://localhost:5000/'+url,
            data: userData,
            withCredentials: true
        })
        .then(function (response) {
            if(response.msg === 'error'){
                alertify.alert('Error!', 'Ha ocurrido un error accesando a la base de datos!<br />Codigo de Error: '+response.detail); 
            }
            else {
                handleCancelButton.bind();                
            }
        })
        .catch(function (error) {
            alertify.alert('Error!', 'No se ha logrado la conexion con el servidor!<br />'+error);
        });

        //console.log(userData);
    }
    handleNameChange(e) { 
        this.setState({nombre: e.target.value}); 
    }
    handlePriceChange(e) { 
        this.setState({precio: e.target.value}); 
    }
  	render() {
  		  var titulo = 'Nuevo';
  		  if(this.props.parametro > 0){
  		  	  titulo = 'Editar';
  		  } 			
  		  return (  	  		  	  	
  	  		<div className="container">
                <div className="content">
                    <div className="table-responsive mt-4">
                        <div className="titulo">{titulo} Tipo de Compra</div>
                    </div>
                    <hr />
                    <div className="table-responsive mb-3">	
  	 		    		<Form>
						  	<Form.Group controlId="formBasicTipoCompra">
						  	  	<Form.Label>Nombre</Form.Label>
						  	  	<Form.Control type="text" onChange={this.handleNameChange.bind(this)}/>						  	  	
						  	</Form.Group>
                <Form.Group controlId="formBasicReciclador">
						  	  	<Form.Label>Precio</Form.Label>
						  	  	<Form.Control type="text" onChange={this.handlePriceChange.bind(this)}/>
						  	</Form.Group>						  						  	
						  	<Button className="float-left mr-3" variant="primary" onClick={this.handleSaveButton.bind(this,this.props.parametro)}>
						  	  	Guardar
						  	</Button>
						  	<Button variant="secondary" onClick={this.handleCancelButton.bind(this)}>
						  	  	Cancelar
						  	</Button>
						</Form>
					</div> 
				</div> 	  	 		       
			</div> 	
  	  	);
  	}
}

export default FormPurchase
