/**
* CLASS Purchases
*
* Contiene el contenedor principal de las compras
*
* @author Hector Morales <warrior1987@gmail.com>
*/

import React, { Component } from 'react';
import DataGrid from '../data_grid/DataGrid';
import globalState from '../configuration/GlobalState';

class DataGridSelect extends Component {  
    constructor(props){
        super(props);
        if(!this.props.params){
            globalState.subscribe( ()=>{           
                if(globalState.getState().type==="windowFormDataSelect"){               
                    this.setState({ showModal: globalState.getState()[this.props.id].visible });
                    if(globalState.getState()[this.props.id].params !== undefined) {//si existen le cargamos los parametros
                        this.setState({ modalParams: globalState.getState()[this.props.id].params }); 
                    }              
                }
            });
        }
    }
    render() {
        console.log(this.props);     
        return (
            <DataGrid titulo='Seleccione...' 
                      funcionClick={this.props.params.funcionClick}  
                      parametro={this.props.parametro}                     
                      colsHeaders={this.props.params.colsHeaders}
                      colsData={this.props.params.colsData} 
                      automatica="false"                    
                      apiField = {this.props.params.apiField}
                      mainContainer='DataGridSelect'/>             
        )
    } 
}

export default DataGridSelect
