/**
* CLASS Purchases
*
* Contiene el contenedor principal de las compras
*
* @author Hector Morales <warrior1987@gmail.com>
*/

import React, { Component } from 'react';
import DataGrid from '../data_grid/DataGrid';
import globalState from '../configuration/GlobalState';

class DataGridSelect extends Component {  
    //var props = '';
    constructor(props){
        super(props);
        this.state = {
            props : this.props.params
        }        
        
    }
    render() {
        var props = this.props.params;
        if(!props){ 
            if(globalState.getState()["windowFormDataSelect"].params !== undefined) {//si existen le cargamos los parametros
                console.log(globalState.getState()["windowFormDataSelect"].params);
                this.setState({ 'props' :  globalState.getState()["windowFormDataSelect"].params});
                props = globalState.getState()["windowFormDataSelect"].params;
            } 
        }
        console.log(this.state.props);     
        return (
            <DataGrid titulo='Seleccione...' 
                      funcionClick={this.state.props.funcionClick}  
                      parametro={this.state.props.parametro}                     
                      colsHeaders={this.state.props.colsHeaders}
                      colsData={this.state.props.colsData} 
                      automatica="false"                    
                      apiField = {this.state.props.apiField}
                      mainContainer='DataGridSelect'/>             
        )
    } 
}

export default DataGridSelect
