import React, { Component } from 'react';
import PurchasesList from './PurchasesList';
import Table from 'react-bootstrap/Table';

class PurchasesContainer extends Component {
    constructor(props) {
        super(props)
        this.state = {  
                        empleados:  { 
                                        tipo_compra: 'CARTON',
                                        reciclador: 'JUAN VELEZ',
                                        peso: '20',
                                        fecha: '2019-02-14'
                                    } 
                     }
    }
  	handleClick(val) {
  	  	this.setState({ justClicked: val });
  	}
    componentWillMount() {
        fetch('http://taller-angular.carlosazaustre.es/empleados')
        .then((response) => {
            return response.json()
        })
        .then((empleados) => {
            this.setState({ empleados: empleados })
        })
        .catch(function(error) {
            console.log(this.state.empleados);
        });
    }
  	render() {
        console.log(this.state.empleados);
        if (this.state.empleados.length > 0) {
            return (
                <Table striped bordered hover>
                    <thead>
                      <tr>
                        <th>#</th>
                        <th>First Name</th>
                        <th>Last Name</th>
                        <th>Username</th>
                      </tr>
                    </thead>
                    <tbody>
                        <PurchasesList listado={this.state.empleados} />
                    </tbody>
                </Table>
            )
        } else {
            return <p className="text-center">Cargando compras...</p>
        }
    } 
}

export default PurchasesContainer
