/**
* CLASS Dashboard
*
* Contiene el contenedor principal de la dashboard
*
* @author Hector Morales <warrior1987@gmail.com>
*/

import React, { Component } from 'react';
import DashboardOption from './DashboardOption';
import {consultarFilas,validarPermiso} from '../api_calls/ApiCalls';
import './dashboard.css';

class DashboardContainer extends Component {
    constructor(props){
        super(props);  
        this.state={

        };        
        this.cargarIndicadores(this.props.date1,this.props.date2);    
    }    
    componentWillUpdate(){
        this.cargarIndicadores(this.props.date1,this.props.date2); 
    }
    cargarIndicadores(date1,date2){
        console.log(date1+'-->'+date2);
        consultarFilas(this.props.apiField,this.state.searchWord,this.state.date1,this.state.date2)
        .then(res => {
            var response = res.data; 
            if (response.msg === "error") {
                alertify.alert('Error!', 'Ha ocurrido un error accesando a la base de datos!<br />Codigo de Error: '+response.detail);
            } else {                
                this.setState({ resultRows: response[0].total })
            }
        })
        .catch( err => {            
            alertify.alert('Error!', 'No se ha logrado la conexion con el servidor!<br />'+err);
        });
    }
  	render() {
        //LOS TIPOS DE GRAFICO SON PieChart y ColumnChart                    
        return ( 
            <div id="DashboardContenedor">
                    <ul>
                        <DashboardOption 
                            tab="dash0" 
                            titulo='Compras' 
                            icono="shopping_cart" 
                            tipo="cifra" 
                            valor="300"
                        />  
                        <DashboardOption 
                            tab="dash1" 
                            titulo='Ventas' 
                            icono="shopping_cart" 
                            tipo="cifra" 
                            valor="100"
                        />                         
                        <DashboardOption 
                            tab="dash2" 
                            titulo='Solicitudes' 
                            icono="shopping_cart" 
                            tipo="grafico"                                                 
                            chartProps= {[
                                            {
                                                chartType : "PieChart",
                                                width     : '500px',
                                                height    : '300px',
                                                loader    : 'Cargando...',
                                                data      :  [
                                                                 ['Task', 'Hours per Day'],
                                                                 ['Work', 11],
                                                                 ['Eat', 2],
                                                                 ['Commute', 2],
                                                                 ['Watch TV', 2],
                                                                 ['Sleep', 7],
                                                             ],
                                                options    : {
                                                                 title: 'My Daily Activities',
                                                             },
                                                rootProps  : { 
                                                                 'data-testid': '1' 
                                                             }
                                            }    
                                         ]}  
                        />                    
                    </ul>
                </div>
        );
    } 
}

export default DashboardContainer
