import React, { Component } from 'react';
import PurchaseTypesList from './PurchaseTypesList';
import Table from 'react-bootstrap/Table';
import axios from 'axios';
import alertify from 'alertifyjs';
import '../../css/alertify.css';

class PurchaseTypesContainer extends Component {
    constructor(props) {
        let  purchaseTypes = [
                                 { 
                                     key: '1',
                                     nombre: 'CARTON',
                                     precio: '5000'                                
                                 },
                                 { 
                                     key: '2',
                                     nombre: 'PLASTICO',
                                     precio: '20000' 
                                 }
                              ];
        super(props);
        this.state = {  
                        purchaseTypes :  purchaseTypes
                     }
    }
  	handleClick(val) {
  	  	this.setState({ justClicked: val });
  	}
    componentWillMount() {
        // fetch('http://taller-angular.carlosazaustre.es/purchases_types')
        // .then((response) => {
        //     return response.json()
        // })
        // .then((purchaseTypes) => {
        //     this.setState({ purchaseTypes: purchaseTypes })
        // })
        // .catch(function(error) {
        //     console.log(error);
        // });
        axios.get('http://localhost:5000/purchase_types', {withCredentials: true})
            .then(res => {
                var response = res.data; 
                if (response.msg === "error") {
                    alertify.alert('Error!', 'Ha ocurrido un error accesando a la base de datos!<br />Codigo de Error: '+response.detail);
                } else {
                    this.setState({ loading: false, redirect: true });
                }
            })
            .catch(err => {
                console.error(err);
                this.setState({ loading: false, redirect: true });
            });
    }
  	render() {       
        if (this.state.purchaseTypes.length > 0) {
            return (
                <Table striped bordered hover>
                    <thead>
                        <tr>
                            <th>No</th>
                            <th>Nombre</th>
                            <th>Precio</th>                            
                            <th style= {{width: '180px'}}>Acciones</th>
                        </tr>
                    </thead>      
                    <PurchaseTypesList listado={this.state.purchaseTypes} funcionClick={this.props.funcionClick}/>                                       
                </Table>
            )
        } else {
            return <p className="text-center">Cargando compras...</p>
        }
    } 
}

export default PurchaseTypesContainer
