import React, { Component } from 'react';
import ReportOption from './ReportOption';
import ReportContainer from './ReportContainer';
import './reports.css';

class Reports extends Component {
    constructor(props, context) {
        super(props, context);     
        this.state = { 
            table : 'blank',
            parameter : ""
        }
    }
  	handleClick(val) {
  	  	alert('hola');
  	}
    actualizarReportContainer(table,param){ 
        this.setState({ table: table });
        this.setState({ parameter : param });   
    }
  	render() {
        let apiUrl = 'http://localhost:5000/';
  	  	return ( 
            <div style={{width:'100%'}}>
                <div id="reportTbar" className="reportTbar" data-role="winTbar" os="windows" >
                    <ReportOption 
                        tab="ReportOption0" 
                        title='Compras' 
                        funcionClick = {this.actualizarReportContainer}                         
                        position="0"
                        optionMenu={[
                                        {
                                            label : 'Informe de Compras',
                                            table : 'purchases',                                            
                                            dateFilter : 'true',                                            
                                        },                                        
                                    ]}
                        apiUrl={apiUrl}
                    />
                    <ReportOption 
                        tab="ReportOption1" 
                        title='Ventas' 
                        funcionClick = {this.actualizarReportContainer}                       
                        position="1"
                    />
                    <ReportOption 
                        tab="ReportOption2" 
                        title='Recicladores' 
                        funcionClick = {this.actualizarReportContainer}                         
                        position="2"
                    /> 
                    <ReportOption 
                        tab="ReportOption2" 
                        title='Clientes' 
                        funcionClick = {this.actualizarReportContainer}                      
                        position="3"
                    />
                    <ReportOption 
                        tab="ReportOption2" 
                        title='Usuarios' 
                        funcionClick = {this.actualizarReportContainer}                        
                        position="4"
                    /> 
                    <ReportOption 
                        tab="ReportOption2" 
                        title='Parametrizaciones' 
                        funcionClick = {this.actualizarReportContainer}                       
                        position="5"
                    />             
                </div>	
                <div id="reportContainer" className="reportContainer" data-role="winTbar" os="windows" >
                    <ReportContainer funcionClick = {this.actualizarReportContainer} table={this.state.table}/>
                </div>
            </div>
  	  	);
  	}
}

export default Reports
