/**
* CLASS Purchases
*
* Contiene el contenedor principal de las compras
*
* @author Hector Morales <warrior1987@gmail.com>
*/

import React, { Component } from 'react';
import DataGrid from '../data_grid/DataGrid';

class Purchases extends Component {  
  	render() {       
        return (
            <DataGrid titulo='Compras' 
                      funcionClick={this.props.funcionClick}  
                      parametro={this.props.parametro}                     
                      colsHeaders={[ 'Fecha Compra','Producto','Reciclador','Peso','Valor Compra' ]}
                      colsData={[ 'fecha_compra','tipo_producto','reciclador','peso','valor_compra' ]} 
                      colsData={[ 
                                    {
                                        type  : 'bd',
                                        label : 'Fecha Compra',
                                        field : 'fecha_compra'
                                    },
                                    {
                                        type  : 'bd',
                                        label : 'Documento',
                                        field : 'documento'
                                    },
                                    {
                                        type  : 'bd',
                                        label : 'Nombre',
                                        field : 'nombre'
                                    },
                                    {
                                        type  : 'bd',
                                        label : 'Direccion',
                                        field : 'direccion'
                                    },
                                    {
                                        type  : 'bd',
                                        label : 'Telefono',
                                        field : 'telefono'
                                    },
                                    {
                                        type  : 'bd',
                                        label : 'Celular',
                                        field : 'celular'
                                    },                                    
                                    {
                                        type  : 'bd',
                                        label : 'Caracterizacion',
                                        field : 'caracterizacion'
                                    },
                                ]} 
                      automatica="true"
                      botonNuevo="true" 
                      formFields={[
                                    {
                                        label : 'Fecha de Compra',
                                        field : 'fecha_compra',
                                        type  : 'date',
                                        validation : '',
                                        required : 'true'
                                    },
                                    {
                                        label : 'Tipo de Producto',
                                        field : 'id_tipo_producto',
                                        type  : 'select',
                                        validation : '',
                                        required : 'true',
                                        dinamic : 'true',
                                        apiField : 'product_types',
                                        valueName : 'nombre'
                                    },                                    
                                    {
                                        label : 'Reciclador',                                        
                                        type  : 'data_select',
                                        field : 'id_reciclador',
                                        dataParams : { 
                                                         fetchData : {
                                                              fieldFetch  : 'nombre',
                                                              idField     : 'id_reciclador',
                                                              valueField  : 'reciclador',
                                                         },
                                                         apiField    : 'reciclators',
                                                         colsHeaders : [ 'Documento','Nombre' ],
                                                         colsData    : [ 'documento','nombre' ],                                                         
                                                     },
                                        validation : '',
                                        required : 'true'                                        
                                    },
                                    {
                                        label : 'Precio',
                                        field : 'peso',
                                        type  : 'text',
                                        validation : 'entero',
                                        required : 'true'
                                    }, 
                                    {
                                        label : '',
                                        field : 'id_empresa',
                                        type  : 'campo_empresa',
                                        validation : '',
                                        required : 'true'                                        
                                    },
                                ]}                     
                      apiField = {'purchases'}
                      mainContainer='Purchases'/>             
        )
    } 
}

export default Purchases
