import React, { Component } from 'react';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import axios from 'axios';
import alertify from 'alertifyjs';
import '../../css/alertify.css';

class FormDataGrid extends Component {
  	
  	constructor(props) {
        super(props);
        // if(this.props.parametro.idRow > 0){
        //     this.state = {nombre: this.props.parametro.nombre,precio: this.props.parametro.precio};
        //     //this.state = {};
        // }
        // else{
        //     this.state = {nombre: '',precio: ''};           
        // }

        this.handleCancelButton = this.handleCancelButton.bind(this);
        this.handleSaveButton = this.handleSaveButton.bind(this);
    }   
    handleCancelButton(){
        this.props.funcionClick(this.props.parametro.mainContainer);        
    }
    handleSaveButton(id){

        // var props = this.props;
        // console.log(props);
        // //GUARDAR/EDITAR LA INFORMACION
        
        // if(this.state.nombre === undefined || this.state.nombre === ''){
        //     alertify.error('Digite el nombre!'); 
        //     return;
        // }   
        // else if(this.state.precio === undefined || this.state.precio === ''){
        //     alertify.error('Digite el precio!'); 
        //     return;
        // }

        var userData = {
            id : id            
        };

        // var url = 'purchase_types';
        // var method = '';

        if(id>0){           
            method = 'put';            
        }
        else{            
            method = 'post';
        } 

        axios({
            method: method,
            url: this.props.parametro.apiUrl,
            data: userData,
            withCredentials: true
        })
        .then(response => {
            var response = response.data;
            if(response.msg === 'error'){
                alertify.alert('Error!', 'Ha ocurrido un error accesando a la base de datos!<br />Codigo de Error: '+response.detail); 
            }
            else {
                this.props.funcionClick(this.props.parametro.mainContainer);              
            }
        })
        .catch(function (error) {
            alertify.alert('Error!', 'No se ha logrado la conexion con el servidor!<br />'+error);
        });

        //console.log(userData);
    }
    handleStateChange(e) { 
        this.setState({nombre: e.target.value}); 
    }
    // handlePriceChange(e) { 
    //     this.setState({precio: e.target.value}); 
    // }
  	render() {
  		  var titulo = 'Agregar';
        var id = 0;
  		  if(this.props.parametro.idRow > null){
  		  	  titulo = 'Editar';
            id = this.props.parametro.id;
  		  } 			
  		  return (  	  		  	  	
  	  		<div className="container">
                <div className="content">
                    <div className="table-responsive mt-4">
                        <div className="titulo">{titulo} {this.props.parametro.titulo}</div>
                    </div>
                    <hr />
                    <div className="table-responsive mb-3">	
  	 		    		<Form>
                            {
                                this.props.parametro.formFields.map((formFields,i) => {
                                    console.log(formFields);
                                })
                            }						  							  						  	
						  	<Button className="float-left mr-3" variant="primary" onClick={this.handleSaveButton.bind(this,id)}>
						  	  	Guardar
						  	</Button>
						  	<Button variant="secondary" onClick={this.handleCancelButton.bind(this)}>
						  	  	Cancelar
						  	</Button>
						</Form>
					</div> 
				</div> 	  	 		       
			</div> 	
  	  	);
  	}
}

export default FormDataGrid
