import React, { Component } from 'react';
import ReportOption from './ReportOption';
import './reports.css';

class Reports extends Component {

  	handleClick(val) {
  	  	alert('hola');
  	}
  	render() {
        let apiUrl = 'http://localhost:5000/';
  	  	return ( 
            <div style={{width:'100%'}}>
                <div id="reportTbar" className="reportTbar" data-role="winTbar" os="windows" >
                    <ReportOption 
                        tab="ReportOption0" 
                        titulo='Compras' 
                        funcionClick={this.props.funcionClick} 
                        componente="PurchaseReport" 
                        position="0"
                        optionMenu={[
                                        {
                                            label : 'Informe de Compras',
                                            table : 'purchases',                                            
                                            dateFilter : 'true',                                            
                                        },                                        
                                    ]}
                        apiUrl={apiUrl}
                    />
                    <ReportOption 
                        tab="ReportOption1" 
                        titulo='Ventas' 
                        funcionClick={this.props.funcionClick} 
                        componente="PurchaseReport" 
                        position="1"
                    />
                    <ReportOption 
                        tab="ReportOption2" 
                        titulo='Recicladores' 
                        funcionClick={this.props.funcionClick} 
                        componente="PurchaseReport" 
                        position="2"
                    /> 
                    <ReportOption 
                        tab="ReportOption2" 
                        titulo='Clientes' 
                        funcionClick={this.props.funcionClick} 
                        componente="PurchaseReport" 
                        position="3"
                    />
                    <ReportOption 
                        tab="ReportOption2" 
                        titulo='Usuarios' 
                        funcionClick={this.props.funcionClick} 
                        componente="PurchaseReport" 
                        position="4"
                    /> 
                    <ReportOption 
                        tab="ReportOption2" 
                        titulo='Parametrizaciones' 
                        funcionClick={this.props.funcionClick} 
                        componente="PurchaseReport" 
                        position="5"
                    />             
                </div>	
                <div id="reportContainer" className="reportContainer" data-role="winTbar" os="windows" >
                    <ReportContainer componente={this.state.componente} funcionClick = {this.actualizarContainer} parametro={this.state.parametro}/>
                </div>
            </div>
  	  	);
  	}
}

export default Reports
