import React from 'react';
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';
import axios from 'axios';
import alertify from 'alertifyjs';
import '../../../css/alertify.css';

class PurchaseTypesRow extends React.Component {
    constructor(props, context) {
        super(props, context);
    
        this.handleConfirmShow = this.handleConfirmShow.bind(this);
        this.handleConfirmClose = this.handleConfirmClose.bind(this);
        this.handleConfirmAction = this.handleConfirmAction.bind(this);
    
        this.state = {
          show: false,
        };
    }
    handleEditButton(param){      
        this.props.funcionClick('FormPurchaseTypes',param);
        //console.log(this.props.funcionClick);
    }
    handleDeleteButton(param){
        alertify.confirm('Confirmacion', 'Esta seguro(a) de eliminar este item?', this.handleConfirmAction.bind(this), function(){});
        //this.handleConfirmShow();
        //alert(param);
        //this.props.funcionClick('FormPurchase',param);
        //console.log(this.props.funcionClick);
    }
    handleConfirmClose() {
        this.setState({ show: false });
    }
  
    handleConfirmShow() {
        this.setState({ show: true });
    }
    handleConfirmAction(id) {
        this.setState({ show: false });
        //CODIGO PARA ELIMINAR LA FILA
        axios.delete('http://localhost:5000/purchase_types',{            
            data: {id : id},
            withCredentials: true
        })
        .then(response => {            
            var response = response.data;
            if(response.msg === 'error'){
                alertify.alert('Error!', 'Ha ocurrido un error accesando a la base de datos!<br />Codigo de Error: '+response.detail); 
            }
            else {
                console.log(this.props);
                this.props.funcionClick('PurchaseTypes');              
            }
        })
        .catch(function (error) {
            alertify.alert('Error!', 'No se ha logrado la conexion con el servidor!<br />'+error);
        });        
    }
    render() {
        var dataRow = this.props.dataRow;
        return(
            <tr>      
                <td>{dataRow.id}</td> 
                <td>{dataRow.nombre}</td>     
                <td>{dataRow.precio}</td>                 
                <td>
                    <div className="float-left mr-3">
                        <Button variant="primary" onClick={this.handleEditButton.bind(this,dataRow)}>EDITAR</Button>
                    </div>
                    <div className="float-left">
                        <Button variant="danger" onClick={this.handleDeleteButton.bind(this,dataRow.id)}>ELIMINAR</Button>
                        <Modal show={this.state.show} onHide={this.handleConfirmClose}>
                            <Modal.Header closeButton>
                                <Modal.Title>Confirmar Borrado</Modal.Title>
                            </Modal.Header>
                            <Modal.Body>Esta seguro(a) de eliminar este item?</Modal.Body>
                            <Modal.Footer>
                                <Button variant="secondary" onClick={this.handleConfirmClose}>
                                    Cancelar
                                </Button>
                                <Button variant="danger" onClick={this.handleConfirmAction.bind(this,dataRow.id)}>
                                    Eliminar
                                </Button>
                            </Modal.Footer>
                        </Modal>
                    </div>
                </td>     
            </tr>
        )
    }
}

export default PurchaseTypesRow
