/**
* GlobalFunctions
*
* Contiene todas las funciones globales de la APP
* @author Hector Morales <warrior1987@gmail.com>
*
*/

import configJson from '../configuration/configuration.json';

export function divMouseOver(e){
	//cambia el color al pasar por encima
	e.target.style.cursor = 'pointer';
	e.target.style.backgroundColor = configJson.fondoMenu+20;
	console.log(e.target.style.cursor);
    // alert(configJson.fondoMenu+20);
}

export function divMouseOut(e){
	//cambia el color al pasar por encima
	e.target.style.cursor = 'default';
	e.target.style.backgroundColor = configJson.fondoMenu+20;
	console.log(e.target.style.cursor);
    // alert(configJson.fondoMenu+20);
}
