/**
* CLASS Smtp
*
* Contiene el contenedor principal de empresas
*
* @author Hector Morales <warrior1987@gmail.com>
*/

import React, { Component } from 'react';
import FormDataGrid from '../data_grid/FormDataGrid';
import {cargarFilas} from '../api_calls/ApiCalls';
import alertify from 'alertifyjs';
import '../../css/alertify.css';

class Smtp extends Component {

    constructor(props){
        super(props);
        this.state={
            idRow : 0
        };    
        let sqlParams = {
                            sqlCols : [
                                'correo',
                                'servidor',
                                'puerto',
                                'autenticacion',
                                'password',
                                'seguridad_smtp'                                
                            ],
                            sqlEmpresa : "true",
                            mode : 'rows'                                                 
                        }

        cargarFilas('companies_smtp','',1,0,'','',sqlParams,'rows')
        .then(res => {
            var response = res.data; 
            if (response.msg === "error") {
                alertify.alert('Error!', 'Ha ocurrido un error accesando a la base de datos!<br />Codigo de Error: '+response.detail);
            } 
            else{
                let idRow = 0;
                if(response.length > 0){
                    idRow = response[0];
                }
                this.setState({ idRow: idRow },()=>{
                   
                            
                    //this.props.funcionClick('FormDataGrid',formParams);               
                });
                // if(response.)                                  
                // this.setState({ loading: false },()=>{
                //     globalState.dispatch({
                //         type   : "userData",
                //         params : response
                //     });                                      
                //     globalState.dispatch({
                //         type   : "nameUser",
                //         params : response[0].primer_nombre.toUpperCase()+' '+response[0].primer_apellido.toUpperCase()
                //     });
                //     globalState.dispatch({
                //         type   : "nameCompany",
                //         params : response1[0].razon_social.toUpperCase()
                //     });
                //     globalState.dispatch({
                //         type   : "imagen_usuario",
                //         params : response[0].imagen_usuario
                //     }); 
                //     globalState.dispatch({
                //         type   : "idRol",
                //         params : response[0].id_rol
                //     });
                // });
                
            }
        })
        .catch( err => {            
            alertify.alert('Error!', 'No se ha logrado la conexion con el servidor!<br />'+err);                            
        });     
    }
    componentWillMount(){
        
    }
  	render() {
        console.log(this.state.idRow);
        let parametro = {
                            idRow : this.state.idRow,
                            mainContainer : 'Smtp',
                            formulario : true,
                            titulo : 'Configuracion SMTP',
                            funcionClick : this.props.funcionClick,                             
                            apiField : 'companies_smtp',
                            formFields :   [{
                                                label : 'Servidor SMTP',
                                                field : 'servidor',
                                                type  : 'text',
                                                validation : '',
                                                required : 'true'                                        
                                            },
                                            {
                                                label : 'Usuario',
                                                field : 'correo',
                                                type  : 'text',
                                                validation : 'email',
                                                required : 'true'                                        
                                            },
                                            {
                                                label : 'Password',
                                                field : 'password',
                                                type  : 'text',
                                                validation : '',
                                                required : 'true'                                        
                                            },
                                            {
                                                label : 'Puerto',
                                                field : 'puerto',
                                                type  : 'text',
                                                validation : 'entero',
                                                required : 'true'                                        
                                            },                                    
                                            {
                                                label : 'Seguridad',
                                                field : 'seguridad_smtp',
                                                type  : 'select',
                                                validation : '',
                                                required : 'true',
                                                dinamic : 'false',
                                                valueName : 'nombre',
                                                options :  [{
                                                                id: 'no',
                                                                nombre: 'Ninguna',
                                                            },
                                                            {
                                                                id: 'tls',
                                                                nombre: 'TLS', 
                                                            },
                                                            {
                                                                id: 'ssl',
                                                                nombre: 'SSL', 
                                                            }],                                        
                                            },  
                                            ,                                    
                                            {
                                                label : 'Autenticacion',
                                                field : 'autenticacion',
                                                type  : 'select',
                                                validation : '',
                                                required : 'true',
                                                dinamic : 'false',
                                                valueName : 'nombre',
                                                options :  [{
                                                                id: 'no',
                                                                nombre: 'No',
                                                            },
                                                            {
                                                                id: 'si',
                                                                nombre: 'Si', 
                                                            }],                                        
                                            },
                                            {
                                                label : '',
                                                field : 'id_empresa',
                                                type  : 'campo_empresa',
                                                validation : '',
                                                required : 'true'                                        
                                            }]                                                                 
                        } 
                     
        return (//carga el componente que contiene la grilla de datos            
            <FormDataGrid 
                parametro = { parametro } 
                        
            />             
        )
    } 
}

export default Smtp
