import React, { Component } from 'react';
import MaterialIcon from 'material-icons-react';
import Dropdown from 'react-bootstrap/Dropdown';
import CustomToggle from './CustomToggleDropdown';
import CustomMenu from './CustomMenuDropdown';
import './reports.css';
//import Container from './Container';

class ReportOption extends Component {
    constructor(props, context) {
        super(props, context); 
        this.handleReportContainerChange = this.handleReportContainerChange.bind(this);         
    }    
    handleReportContainerChange(){//CALLBACK PARA ACTUALIZAR EL CONTENEDOR DE REPORTE
        let table = 'blank';
        console.log(this.props.optionMenu);
        if(this.props.optionMenu !== undefined){
            table = this.props.optionMenu[0].table;
        }
        this.props.funcionClick(table);
        console.log(table);
    }
  	render() {
        let position = this.props.position*120;        
  	  	return (
            <Dropdown id={this.props.tab} className="reportBtn" style={{width:'120px',position:'absolute',height:'60px',left:position+'px'}}>  
                <Dropdown.Toggle as={CustomToggle} id="dropdown-custom-components"> 
                    <div style={{width:'120px'}} data-role="win-btn" data-state="enable"> 
                        <div style={{width:'100%',textAlign:'center'}}> 
                            <MaterialIcon size={24} icon="poll" invert />  
                        </div>             
                        <button className="save" os="windows">{this.props.title}</button>
                    </div>
                </Dropdown.Toggle>
                <Dropdown.Menu  style={{ marginTop: '0px',width:'230px'}}>
                    {   
                        this.props.optionMenu ? 
                            this.props.optionMenu.map((optionMenu,i) => {
                                return (<Dropdown.Item eventKey="1" key={i} onClick={this.handleReportContainerChange.bind(this)}>
                                    <div style={{height:'25px'}}>
                                        <div style={{width:'30px',float:'left'}}>
                                            <MaterialIcon size={24} icon="poll" /> 
                                        </div>
                                        <div style={{color:'#000',height:'25px',float:'left',verticalAlign:'middle',paddingTop:'5px'}}>
                                            {optionMenu.label}
                                        </div>
                                    </div>
                                </Dropdown.Item>)
                            }) 
                        : 'hehehehe'  
                    }                                                                                  
                </Dropdown.Menu>
            </Dropdown>
  				    				
			  );
  	}
}

export default ReportOption

