*/

import React, { Component } from 'react';
import MaterialIcon from 'material-icons-react';
import Form from 'react-bootstrap/Form';
import './desktop.css';

class FormDataUser extends Component {
    handleContainerChange(){
        this.props.funcionClick(this.props.componente);        
    }
  	render() {
  	  	return (
  				  <div className="container">
                <div className="content">
                    <div className="table-responsive mt-4">
                        <div className="titulo">Datos del Usuario</div>
                    </div>
                    <hr />
                    <div className="table-responsive mb-3">
                        <Form>
                            <Form.Group key= {i} controlId="formBasicTipoCompra">
                                 <Form.Label>{formFields.label}</Form.Label>
                                 <Form.Control name = {formFields.field} type={formFields.type} onChange={this.handleStateChange.bind(this)} value={this.state[formFields.field]}/>                               
                            </Form.Group>
                        </Form>

                    </div> 
                </div>
            </div>  				
			  );
  	}
}

export default FormDataUser
