/**
* CLASS DocumentTypes
*
* Contiene el contenedor principal de tipos de documento
*
* @author Hector Morales <warrior1987@gmail.com>
*/

import React, { Component } from 'react';
import DataGrid from '../data_grid/DataGrid';

class DocumentTypes extends Component {
  	render() {     
        let sqlParams = {
                            sqlCols : [
                                'T1.id',
                                '(T1.peso * PT.precio_compra) AS valor_compra',
                                'DATE_FORMAT(T1.fecha_compra,"%Y-%m-%d") AS fecha_compra',
                                'PT.id AS id_tipo_producto',
                                'PT.nombre AS tipo_producto',
                                'R.id AS id_reciclador',
                                'R.nombre AS reciclador',
                                'T1.peso',
                                'T1.id_empresa'
                            ],
                            sqlJoin : [
                                'INNER JOIN product_types AS PT ON (PT.id = T1.id_tipo_producto)', 
                                'INNER JOIN reciclators AS R ON (R.id = T1.id_reciclador)'
                            ],
                            fieldSearch : [
                                'PT.nombre',
                                'R.nombre',
                                'T1.peso'
                            ],
                            sqlEmpresa : "false",                            
                            filtroFechas : "true", 
                            fieldFechas : "T1.fecha_compra"                    
                        };                 
        return (//carga el componente que contiene la grilla de datos            
            <DataGrid titulo='TÃ­pos de Documento' 
                      funcionClick={this.props.funcionClick} 
                      parametro={this.props.parametro}
                      colsData={[ 
                                    {
                                        type  : 'bd',
                                        label : 'Nombre',
                                        field : 'nombre'
                                    },
                                ]} 
                      sqlParams = { sqlParams }
                      automatica="true"
                      botonNuevo="true"
                      formFields={[
                                    {
                                        label : 'Nombre',
                                        field : 'nombre',
                                        type  : 'text',
                                        validation : 'mayusculas',
                                        required : 'true'
                                    }                                    
                                ]}                     
                      apiField={'document_types'}
                      mainContainer='DocumentTypes'/>              
        )
    } 
}

export default DocumentTypes
