import React, { Component } from 'react';
import NameUser from './NameUser';
import OptionMenu from './OptionMenu';
import Container from './Container';
import ReactDOM from 'react-dom';
import Reciclators from '../reciclators/Reciclators';
import Costumers from '../costumers/Costumers';
import Purchases from '../purchases/Purchases';
import ControlPanel from '../control_panel/ControlPanel';
import './desktop.css';

class Desktop extends Component {
	LayoutContenido(props) {
	 	switch(props.contenido) {
 			case 1: return <Purchases/>; break;
  			case 2: return <Costumers/>; break;
  			case 3: return <Reciclators/>; break;
			default: return <ControlPanel/>; break;
		}
	}
	constructor(props){
        super(props)

        this.state = {
          contenido: 1//Aqui es el valor que le pasare al props de la función
        };

        this.LayoutContenido = this.LayoutContenido.bind(this)

        //this.hijo = React.createRef()

    }
  	render() {
  	  	return (  	  		  
  	  		<div className="App">                 
	 		    <div id="pestanas" className="navigationDesk">
	 		    	<div className="LogoCabecera"></div>
	 		    	<OptionMenu funcion = {this.LayoutContenido}/>
	 		    </div>
	 		    <div id="cabeceraDesk" className="cabeceraDesk">
	 		    	<NameUser className="ContentUser" />  	    		
	 		    </div>
	    		<div id="contenidopestanas" className="contentDesk">
	    			
	    		</div>	 		     
	        </div>   	
		);
  	}
}

